using namespace std;
typedef struct node&#123;
    int data;
    int ret;
    struct node* lchild;
    struct node* rchild;
    struct node* parent;
&#125;node;
typedef node* tree;

void posttravel(tree head)&#123;
    if(!head)&#123;
    &#125;
    else&#123;
        posttravel(head-&gt;lchild);
        posttravel(head-&gt;rchild);
        cout &lt;&lt; head-&gt;data;
        if(head-&gt;parent)&#123;
            cout &lt;&lt;&quot; &quot;;
        &#125;
    &#125;
&#125;
int main()&#123;
    int n;
    string sign;
    tree head&#x3D;NULL;
    cin &gt;&gt; n;
    for (int i&#x3D;0;i&lt;2*n;++i)&#123;
        cin &gt;&gt; sign;
        if (sign&#x3D;&#x3D;&quot;Push&quot;)&#123;
            if(!head)&#123;
                tree temp&#x3D;(tree)malloc(sizeof(node));
                temp-&gt;lchild&#x3D;NULL;
                temp-&gt;rchild&#x3D;NULL;
                cin &gt;&gt; temp-&gt;data;
                temp-&gt;ret&#x3D;1;
                head&#x3D;temp;
            &#125;
            else if(!head-&gt;lchild)&#123;
                tree temp&#x3D;(tree)malloc(sizeof(node));
                temp-&gt;parent&#x3D;head;
                head-&gt;lchild&#x3D;temp;
                temp-&gt;lchild&#x3D;NULL;
                temp-&gt;rchild&#x3D;NULL;
                cin &gt;&gt; temp-&gt;data;
                temp-&gt;ret&#x3D;1;
                head&#x3D;temp;
            &#125;
